pub enum Error {
    UnknownField {
        field_name: Option<String>,
    },
    IncompleteFieldData {
        field_name: Option<String>,
    },
    IncompleteHeaders,
    ReadHeaderFailed(BoxError),
    DecodeHeaderName {
        name: String,
        cause: BoxError,
    },
    DecodeHeaderValue {
        value: Vec<u8>,
        cause: BoxError,
    },
    IncompleteStream,
    FieldSizeExceeded {
        limit: u64,
        field_name: Option<String>,
    },
    StreamSizeExceeded {
        limit: u64,
    },
    StreamReadFailed(BoxError),
    LockFailure(BoxError),
    NoMultipart,
    DecodeContentType(BoxError),
    NoBoundary,
    __Nonexhaustive,
}
